import { Badge, Button, Card, CardBody, CardTitle, Row, Col } from "reactstrap";
import axios from 'axios';
import { Bounce, toast } from 'react-toastify';
import React, { useState } from 'react'

const Add= () => {

  const [formdata, setFormdata] = useState({})

    
  // Handling Input inside Form
  const handleInput =(e)=>{
      const {name, value} = e.target;
      setFormdata({...formdata, [name]: value})
  }

  // Handling Submit Button On Form
  const handleSubmit = async(e)=>{
      e.preventDefault();

      try{

      const res = await axios.post(`http://127.0.0.1:8000/destinationCreate/`, formdata,
          {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json'
              }
          })

          if (res.status === 201){
              toast("ðŸŽ‰ Book Added Successfully !!",
              {
                position: "top-center",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
              }
              
          )}

      }catch(error){
          console.log(error)
      }
  }


  return (
    <div>

        <Card>
        <CardTitle tag="h6" className="border-bottom p-3 mb-0">
          <i className="bi bi-plus-lg">  </i>
          Add Book
        </CardTitle>
        <CardBody className="">

        <form onSubmit={handleSubmit}>
                <div className='form-group '>
                    <label class="form-label">Book Title </label>
                    <input type="text" name ='title' class="form-control" onChange={handleInput}/>

                    <label class="form-label">Book Author </label>
                    <input type="text" class="form-control" name= 'author' onChange={handleInput}/>

                    <label class="form-label">Book ISBN </label>
                    <input type="text" class="form-control" name='isbn' onChange={handleInput}/>

                    <label  class="form-label">Book Price </label>
                    <input type="text" class="form-control"  name='price' onChange={handleInput}/>

                  <br></br>

                    <button type="submit" class="btn btn-success">Submit</button>


                </div>
            </form>

        </CardBody>
        </Card>
    </div>
  );
};

export default AddBooks;
