import React, { useState, useEffect } from "react";
import axios from 'axios'
import { Bounce, ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {
  Card,
  CardBody,
  CardTitle,
} from "reactstrap";

const Destinations = () => {
  
  const [data, setData] = useState([])
  const [update, setUpdate] = useState({})

  useEffect(()=>{
    axios
    .get('http://127.0.0.1:8000/destinationCreate/')
    .then((response)=>{
        setData(response.data)
    })
    .catch((error)=>{
        console.log(error)
    })
},[data])

   // GET DETAILS TO UPDATE -- FUNCTION
   const viewDestination=(id)=>{
    fetch(`http://127.0.0.1:8000/destinationUpdate/${id}/`)
    .then(response=>response.json())
    .then(res=>setUpdate(res))
  }

      // UPDATE DETAILS ON SUBMIT BUTTON CONFUIRGATION
      const handleUpdatesubmit = async(e, id)=>{
        e.preventDefault();
        const requestData = {
          id: update.id,
          place_name: update.place_name,
          weather : update.weather,
          state : update.state,
          district : update.district,
          google_map_link : update.google_map_link,
          image : update.img,
          description : update.description,
        };
        const response = await axios.put(`http://127.0.0.1:8000/destinationUpdate/${id}/`, requestData, { 
          headers:{
            'Content-Type':'multipart/form-data'
          },
      });
      toast("ðŸ¤© Details of the Book Updated", {
        position: "top-center",
        autoClose: 4000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
        transition: Bounce,
  
      })
    }

    const handleInputChange = (event, fieldName)=>{
      const value = event.target.value;
       setUpdate((prevUpdate)=>({
         ...prevUpdate, 
         [fieldName]: value,
       }))
     }



  return (
    <>
      <Card>
        <CardTitle tag="h6" className="border-bottom p-3 mb-0">
          <i className="bi bi-globe-central-south-asia"> </i>
          Destinations
        </CardTitle>
        <CardBody className="">

        <table class="table table-striped shadow">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Destination</th>
      <th scope="col">Destination State</th>
      <th scope="col">Destination District</th>
      <th scope="col">OPERATIONS</th>
    </tr>
  </thead>
  <tbody>

    {/* // If Not Using Pagination Use => data.map // */}
  {data.map((dest, index)=>{
        return(
            <tr key={index}>
            <th scope="row">{index + 1}</th>
            <td>{dest.place_name}</td>
            <td>{dest.state}</td>
            <td>{dest.district}</td>
            <td>
            <button className='btn btn-primary'  data-bs-toggle="modal" data-bs-target="#exampleModalView" onClick={()=>{viewDestination(dest.id)}}>View</button> &nbsp;
            <button className='btn btn-danger'  data-bs-toggle="modal" data-bs-target="#exampleModalsDelete">Delete</button>&nbsp;
            </td>
          </tr>
        )
    })}
   
    </tbody>
    </table>
    </CardBody>
    </Card>
   
    {/* ------- View Destination ------- */}
  <div class="modal fade" id="exampleModalView" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">{update.place_name},{update.state}</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>{update.description}</p><br></br>
        <p><b>State: {update.state}</b></p>
        <p><b>District: {update.district}</b></p>
        <p><b>Temprature: {update.weather}Â°C</b></p>
        <p><b>Direction: </b><a href={update.google_map_link}><b>{update.place_name} <i class="bi bi-box-arrow-up-right"></i></b></a></p>
        <p><b>{update.image}</b></p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModalUpdate">Update</button>
      </div>
    </div>
    </div>
  </div>

  {/* ------- View Destination ENDS HERE ------- */}

  {/* ------- Update Destination ------- */}

  <div class="modal fade" id="exampleModalUpdate" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Update Details of Destination: {update.place_name}</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      <div className='container'>
            <form onSubmit={(e)=>handleUpdatesubmit(e, update.id)}>
                <div className='form-group'>
                    <label class="form-label">Destination</label>
                    <input type="text" class="form-control"  value={update.place_name} onChange={(event)=>handleInputChange(event, 'place_name')}/>

                    <label class="form-label">Destination State</label>
                    <input type="text" class="form-control"  value={update.state} onChange={(event)=>handleInputChange(event, 'state')}/>

                    <label class="form-label">Destination District</label>
                    <input type="text" class="form-control"  value={update.district} onChange={(event)=>handleInputChange(event, 'district')}/>

                    <label  class="form-label">Destination Temprature</label>
                    <input type="text" class="form-control"  value={update.weather} onChange={(event)=>handleInputChange(event, 'weather')}/>

                    <label  class="form-label">Destination Direction Link</label>
                    <input type="text" class="form-control"  value={update.google_map_link} onChange={(event)=>handleInputChange(event, 'google_map_link')}/>

                    <label  class="form-label">Destination Image</label>
                    <input type="text" class="form-control"  value={update.image} onChange={(event)=>handleInputChange(event, 'weather')}/>
                
                   <br></br>

                    <button type="submit" class="btn btn-success">Update Details</button>


                </div>
            </form>
         </div>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
    </div>
    </div>

    </>
  );
};

export default Destinations;
