import { Badge, Button, Card, CardBody, CardTitle, Row, Col } from "reactstrap";
import axios from 'axios';
import { Bounce, toast } from 'react-toastify';
import React from 'react'

const addDestination = () => {

  const [formdata, setFormdata] = useState({})

    
  // Handling Input inside Form
  const handleInput =(e)=>{
      const {name, value} = e.target;
      setFormdata({...formdata, [name]: value})
  }

  // Handling Submit Button On Form
  const handleSubmit = async(e)=>{
      e.preventDefault();

      try{

      const res = await axios.post(`http://127.0.0.1:8000/addBook/`, formdata,
          {
              method: 'POST',
              headers: {
                  'Content-Type': 'multipart/form-data'
              }
          })

          if (res.status === 201){
              toast("ðŸŽ‰ Destination Added Successfully !!",
              {
                position: "top-center",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
              }
              
          )}

      }catch(error){
          console.log(error)
      }
  }

    // Handling Image File Input
    const handleInputImageFile = (e)=>{
      const file = e.target.files[0];
      const formDataImage = new FormData();
      formDataImage.append('name', formdata.name);
      formDataImage.append('email', formdata.email);
      formDataImage.append('phone', formdata.phone);
      formDataImage.append('position', formdata.position);
      formDataImage.append('department', formdata.department);
      formDataImage.append('img', file);

      setFormdata(formDataImage)
  }

  return (
    <>



    </>
  );
};

export default addDestination;
