import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardTitle,
} from "reactstrap";

const Destinations = () => {
  
  const [data, setData] = useState([])
  const [update, setUpdate] = useState({})

  return (
    <>
      <Card>
        <CardTitle tag="h6" className="border-bottom p-3 mb-0">
          <i className="bi bi-globe-central-south-asia"> </i>
          Destinations
        </CardTitle>
        <CardBody className="">

        <table class="table table-striped shadow">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">TITLE</th>
      <th scope="col">Author</th>
      <th scope="col">ISBN</th>
      <th scope="col">PRICE</th>
      <th scope="col">OPERATIONS</th>
    </tr>
  </thead>
  <tbody>

    {/* // If Not Using Pagination Use => data.map // */}
  {records.map((book, index)=>{
        return(
            <tr key={index}>
            <th scope="row">{index + 1}</th>
            <td>{book.place_name}</td>
            <td>{book.author}</td>
            <td>{book.isbn}</td>
            <td><i class="bi bi-currency-rupee"></i>{book.price}</td>

            <td>
            <button className='btn btn-primary' onClick={()=>{updateBook(book.id)}} data-bs-toggle="modal" data-bs-target="#exampleModalUpdate">Update</button> &nbsp;
            <button className='btn btn-danger' onClick={()=>{updateBook(book.id)}} data-bs-toggle="modal" data-bs-target="#exampleModalsDelete">Delete</button>
            </td>
          </tr>
        )
    })}
   
    </tbody>
    </table>



        </CardBody>
      </Card>
      
    </>
  );
};

export default Destinations;
