import React, { useState } from 'react';
import axios from 'axios';
import { toast, Bounce } from 'react-toastify';

const UpdateDestinationForm = ({ id }) => {
    const [data, setData] = useState({
        place_name: '',
        weather: '',
        state: '',
        district: '',
        google_map_link: '',
        description: ''
    });
    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setData(prevData => ({ ...prevData, [name]: value }));
    };

    const handleInputImage = (e) => {
        const file = e.target.files[0];
        setImage(file);
        setImagePreview(URL.createObjectURL(file));
    };

    const handleUpdatesubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('place_name', data.place_name);
        formData.append('weather', data.weather);
        formData.append('state', data.state);
        formData.append('district', data.district);
        formData.append('google_map_link', data.google_map_link);
        formData.append('image', image);
        formData.append('description', data.description);

        try {
            const response = await axios.put(`http://127.0.0.1:8000/destinationUpdate/${id}/`, formData, { 
                headers: {
                    'Content-Type': 'multipart/form-data'
                },
            });
            toast("ü§© Details of the Destination Updated", {
                position: "top-center",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        } catch (error) {
            console.error("There was an error updating the destination details:", error);
            toast.error("‚ùå Failed to update destination details. Please try again.", {
                position: "top-center",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        }
    };

    return (
        <form onSubmit={handleUpdatesubmit}>
            <input type="text" name="place_name" value={data.place_name} onChange={handleInputChange} placeholder="Place Name" />
            <input type="text" name="weather" value={data.weather} onChange={handleInputChange} placeholder="Weather" />
            <input type="text" name="state" value={data.state} onChange={handleInputChange} placeholder="State" />
            <input type="text" name="district" value={data.district} onChange={handleInputChange} placeholder="District" />
            <input type="text" name="google_map_link" value={data.google_map_link} onChange={handleInputChange} placeholder="Google Map Link" />
            <input type="file" name="image" onChange={handleInputImage} />
            {imagePreview && <img src={imagePreview} alt="Selected" style={{ width: '130px', height: '130px' }} />}
            <textarea name="description" value={data.description} onChange={handleInputChange} placeholder="Description"></textarea>
            <button type="submit">Update Destination</button>
        </form>
    );
};

export default UpdateDestinationForm;
